    private void g_apagarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_g_apagarActionPerformed
        try {
            // validando dados da interface gr�fica.
            int id = 0;
            if (this.jTableEmprestimo.getSelectedRow() == -1) {
                throw new Mensagens("Primeiro Selecione um Aluno para APAGAR");
            } else {
                id = Integer.parseInt(this.jTableEmprestimo.getValueAt(this.jTableEmprestimo.getSelectedRow(), 0).toString());
            }

            // retorna 0 -> primeiro bot�o | 1 -> segundo bot�o | 2 -> terceiro bot�o
            int resposta_usuario = JOptionPane.showConfirmDialog(null, "Tem certeza que deseja APAGAR este Emprestimo ?");

            if (resposta_usuario == 0) {// clicou em SIM

                // envia os dados para o Aluno processar
                if (this.objemprestimo.DeleteEmprestimoBD(id)) {

                    // limpa os campos
//                    this.g_idCliente.setText("");
//                    this.g_idFerramenta.setText("");
//                    this.g_dataEmprestimo.setText("");
//                    this.g_dataDevolucao.setText("");
                    JOptionPane.showMessageDialog(rootPane, "Emprestimo Apagada com Sucesso!");

                }

            }

            System.out.println(this.objemprestimo.getMinhaListae().toString());

        } catch (Mensagens erro) {
            JOptionPane.showMessageDialog(null, erro.getMessage());
        } finally {
            // atualiza a tabela.
            carregaTabela();
        }
    }//GEN-LAST:event_g_apagarActionPerformed

