    private void carregaTabela() {

        DefaultTableModel modelo = (DefaultTableModel) this.jTableEmprestimo.getModel();
        modelo.setNumRows(0);

        ArrayList<Emprestimo> minhalista = new ArrayList<>();
        minhalista = objemprestimo.getMinhaListae();

        for (Emprestimo a : minhalista) {
            modelo.addRow(new Object[]{
                a.getId_Emprestimo(),
                a.getIdCliente(),
                a.getIdFerramenta(),
                a.getDataEmprestimo(),
                a.getDataDevolucao(),
            });
        }
    }
